// Buzzer Melody Player
// Play musical notes using tone() function

const int BUZZER_PIN = 8;

// Musical note frequencies (Hz)
#define NOTE_C4  262
#define NOTE_D4  294
#define NOTE_E4  330
#define NOTE_F4  349
#define NOTE_G4  392
#define NOTE_A4  440
#define NOTE_B4  494
#define NOTE_C5  523
#define NOTE_REST 0

// "Twinkle Twinkle Little Star" melody
int melody[] = {
  NOTE_C4, NOTE_C4, NOTE_G4, NOTE_G4, NOTE_A4, NOTE_A4, NOTE_G4,
  NOTE_F4, NOTE_F4, NOTE_E4, NOTE_E4, NOTE_D4, NOTE_D4, NOTE_C4,
  NOTE_G4, NOTE_G4, NOTE_F4, NOTE_F4, NOTE_E4, NOTE_E4, NOTE_D4,
  NOTE_G4, NOTE_G4, NOTE_F4, NOTE_F4, NOTE_E4, NOTE_E4, NOTE_D4,
  NOTE_C4, NOTE_C4, NOTE_G4, NOTE_G4, NOTE_A4, NOTE_A4, NOTE_G4,
  NOTE_F4, NOTE_F4, NOTE_E4, NOTE_E4, NOTE_D4, NOTE_D4, NOTE_C4
};

// Note durations: 4 = quarter note, 8 = eighth note, etc.
int noteDurations[] = {
  4, 4, 4, 4, 4, 4, 2,
  4, 4, 4, 4, 4, 4, 2,
  4, 4, 4, 4, 4, 4, 2,
  4, 4, 4, 4, 4, 4, 2,
  4, 4, 4, 4, 4, 4, 2,
  4, 4, 4, 4, 4, 4, 2
};

int numNotes = sizeof(melody) / sizeof(melody[0]);

void setup() {
  Serial.begin(9600);
  Serial.println("Buzzer Melody Player");
  Serial.println("Playing: Twinkle Twinkle Little Star");
}

void playMelody() {
  for (int i = 0; i < numNotes; i++) {
    // Calculate note duration
    int noteDuration = 1000 / noteDurations[i];

    if (melody[i] != NOTE_REST) {
      tone(BUZZER_PIN, melody[i], noteDuration);
      Serial.print("Note: ");
      Serial.print(melody[i]);
      Serial.println(" Hz");
    } else {
      Serial.println("Rest");
    }

    // Pause between notes (30% of note duration)
    int pauseBetweenNotes = noteDuration * 1.30;
    delay(pauseBetweenNotes);

    // Stop tone
    noTone(BUZZER_PIN);
  }
}

void loop() {
  Serial.println("\n=== Starting melody ===");
  playMelody();
  Serial.println("=== Melody complete ===");

  delay(2000);  // Wait before playing again
}
