// LCD Hello World - I2C Display
// Display text on 16x2 LCD using Wire library

#include <Wire.h>
#include <LiquidCrystal_I2C.h>

// LCD address is typically 0x27 or 0x3F
LiquidCrystal_I2C lcd(0x27, 16, 2);

void setup() {
  // Initialize LCD
  lcd.init();
  lcd.backlight();

  Serial.begin(9600);
  Serial.println("LCD Hello World Demo");

  // Display welcome message
  lcd.setCursor(0, 0);
  lcd.print("Hello, FUNDI!");
  lcd.setCursor(0, 1);
  lcd.print("LCD Working :)");
}

int counter = 0;

void loop() {
  // Update counter every second
  delay(1000);
  counter++;

  // Display on second line
  lcd.setCursor(0, 1);
  lcd.print("Count: ");
  lcd.print(counter);
  lcd.print("     ");  // Clear remaining chars

  Serial.print("Counter: ");
  Serial.println(counter);
}
