// Servo Motor Sweep
// Sweeps the servo back and forth from 0 to 180 degrees

#include <Servo.h>

Servo myServo;

const int SERVO_PIN = 9;
int angle = 0;
int step = 1;

void setup() {
  myServo.attach(SERVO_PIN);

  Serial.begin(9600);
  Serial.println("Servo Sweep Demo");
  Serial.println("Sweeping 0-180 degrees");

  // Start at 0 degrees
  myServo.write(0);
  delay(500);
}

void loop() {
  // Move servo to current angle
  myServo.write(angle);

  // Print current angle
  Serial.print("Angle: ");
  Serial.print(angle);
  Serial.println(" degrees");

  // Update angle for next iteration
  angle += step;

  // Reverse direction at limits
  if (angle >= 180) {
    angle = 180;
    step = -1;
    Serial.println(">> Reversing direction (going back)");
  } else if (angle <= 0) {
    angle = 0;
    step = 1;
    Serial.println(">> Reversing direction (going forward)");
  }

  delay(15);  // Small delay for smooth movement
}
